			["xOffset"] = 0,
			["displayText"] = "%c",
			["customText"] = "function()\n    if aura_env.state and aura_env.state.name and aura_env.config[\"list\"] then\n        if UnitExists(aura_env.state.name) then\n            return aura_env.state.mark..\" \"..WA_ClassColorName(aura_env.state.name)\n        else\n            return aura_env.state.mark..\" \"..aura_env.state.name\n        end\n    end\nend",
