				["init"] = {
					["custom"] = "aura_env.icons= { \n    [1]  = \n    \" \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_1:12\\124t\",\n    [2] =\n    \" \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_2:12\\124t\", \n    [3] =\n    \" \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_3:12\\124t\", \n    [4] =\n    \" \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_4:12\\124t\", \n    [5] =\n    \" \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_5:12\\124t\", \n    [6] =\n    \" \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_6:12\\124t\",   \n    [7] =\n    \" \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_7:12\\124t\",\n    [8] =\n    \" \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_8:12\\124t\",\n    [9] =\n    \" Right of \\124TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_1:12\\124t\",\n    [10] =\n    \" 10\",\n    [11] =\n    \" 11\",\n    [12] =\n    \" 12\",\n    [13] =\n    \" 13\",\n    [14] =\n    \" 14\",\n    [15] =\n    \" 15\",\n}\n\naura_env.list = aura_env.config[\"list\"]\naura_env.max = 9\naura_env.cast = 344496\naura_env.unit = \"boss2target\"\naura_env.players = {}\naura_env.backup = {}\naura_env.num = 0\naura_env.alert = 1\naura_env.count = 1\naura_env.countb = 1\naura_env.assign = false\n\nif aura_env.config[\"debug\"] then\n    aura_env.cast = 8936 -- debug\n    aura_env.unit = \"target\"\nend\n\naura_env.colorcode = function(name)\n    local length = string.len(name)\n    if string.sub(name, 2, 3) == \"|c\" then -- checking if name starts with colorcode\n        if string.sub(name, length-1) == \"|r\" then -- checking if end of name ends the colorcode or not (if same color is used twice in a row it might not end the color until 2nd name)\n            return string.sub(name, 12, length-3)\n        else\n            return string.sub(name, 12)\n        end\n    elseif string.sub(name, length-1) == \"|r\" then -- checking if only end of colorcode is present\n        return string.sub(name, 1, length-3)\n    else\n        return name\n    end\nend\n\n\naura_env.ERT = function()\n    local text = _G.VExRT.Note.Text1\n    aura_env.assign = false\n    for line in string.gmatch(text,'[^\\r\\n]+') do\n        local start = strsplit(\" \", line)\n        if string.lower(start) == \"end\" then\n            aura_env.assign = false\n            aura_env.assign2 = false\n        elseif string.lower(start) == \"backup\" then\n            aura_env.assign = false\n            aura_env.assign2 = true\n        elseif aura_env.assign2 then\n            aura_env.backup[aura_env.countb] = aura_env.colorcode(start)\n            aura_env.countb = aura_env.countb+1\n        elseif aura_env.assign then\n            aura_env.players[aura_env.count] = aura_env.colorcode(start)\n            aura_env.count = aura_env.count+1\n            \n            \n        elseif string.lower(start) == \"start\" then\n            aura_env.players = {}\n            aura_env.backup = {}\n            aura_env.num = 0\n            aura_env.alert = 1\n            aura_env.count = 1\n            aura_env.countb = 1\n            aura_env.assign = true\n            aura_env.assign2 = false\n        end\n    end\nend",
