						["event"] = "Health",
						["subeventPrefix"] = "SPELL",
						["unit"] = "player",
						["custom"] = "function(states, event, ...)\n    if event == \"OPTIONS\" then\n        states[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            name = UnitName(\"player\"),\n            mark = aura_env.icons[1], \n            index = 1,\n        }\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.ERT()\n        aura_env.num = 0\n        aura_env.alert = 1\n        aura_env.looped = false\n        aura_env.assigned = {}\n    elseif event == \"RELOE_STATE_UPDATE\" then\n        if ... then\n            if aura_env.list then\n                for i=1, aura_env.num do\n                    if aura_env.players[i] then\n                        states[i] = {\n                            show = true,\n                            changed = true,\n                            progressType = \"static\",\n                            name = aura_env.players[i],\n                            mark = aura_env.icons[i], \n                            index = i,\n                        }\n                    end\n                end\n                return true\n            end\n        else\n            if UnitExists(aura_env.unit) then\n                WeakAuras.ScanEvents(\"RELOE_GLOW_ALERT\", aura_env.unit)\n                if UnitIsUnit(aura_env.unit, \"player\") then\n                    WeakAuras.ScanEvents(\"RELOE_CAST_ALERT\", aura_env.alert)\n                end\n            end\n            for i=1, aura_env.num do\n                if aura_env.num == aura_env.max and aura_env.looped and i == aura_env.alert then -- preventing soak alert for a person that currently has no zone on the ground\n                else\n                    if (not aura_env.players[i]) or aura_env.players[i] == UnitName(aura_env.unit) or UnitIsDead(aura_env.players[i]) then -- needs backup\n                        for j=1, #aura_env.backup+1 do\n                            local unit = aura_env.backup[j]\n                            if unit then -- checking if a backup still exists\n                                if (not (UnitIsDead(unit))) and (not (UnitName(aura_env.unit) == unit)) and (not (aura_env.assigned[unit])) then -- check if backup is viable\n                                    WeakAuras.ScanEvents(\"RELOE_GLOW_ALERT\", unit)\n                                    if UnitName(\"player\") == UnitName(unit) then\n                                        WeakAuras.ScanEvents(\"RELOE_BACKUP_ALERT\", i)\n                                    end\n                                    aura_env.assigned[unit] = true\n                                    break\n                                end\n                            else\n                                WeakAuras.ScanEvents(\"RELOE_BACKUP_ALERT2\", i)\n                                break\n                            end\n                        end\n                    else -- all is fine, player can soak\n                        WeakAuras.ScanEvents(\"RELOE_GLOW_ALERT\", aura_env.players[i])\n                        if aura_env.players[i] == UnitName(\"player\") then\n                            WeakAuras.ScanEvents(\"RELOE_SOAK_ALERT\", i)\n                        end\n                    end\n                end\n            end\n            return true\n        end\n    else\n        local timestamp, subevent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellID = ...\n        if subevent == \"SPELL_CAST_SUCCESS\" and spellID == aura_env.cast then\n            aura_env.num = (aura_env.num < aura_env.max and aura_env.num+1) or aura_env.num\n            aura_env.alert = (aura_env.alert < aura_env.max and aura_env.alert+1) or 1\n            aura_env.looped = (aura_env.alert == aura_env.max and true) or aura_env.looped -- checking if all 8 zones are dropped already\n            WeakAuras.ScanEvents(\"RELOE_STATE_UPDATE\", true)\n            \n        elseif subevent == \"SPELL_CAST_START\" and spellID == aura_env.cast then\n            for unit in WA_IterateGroupMembers() do\n                aura_env.assigned[UnitName(unit)] = false\n            end\n            C_Timer.After(0.2, function()\n                    WeakAuras.ScanEvents(\"RELOE_STATE_UPDATE\", false)\n            end)\n        end\n    end\nend\n\n\n",
