			["actions"] = {
				["start"] = {
				},
				["init"] = {
					["custom"] = "aura_env.priority = {}\nfor unit in aura_env.config.priority:gmatch(\"%S+\") do\n    if UnitExists(unit) then\n        local name, realm = UnitName(unit)\n        if realm then name = name .. \"-\" .. realm end -- Use Name-Realm to match CLEU destName\n        aura_env.priority[name] = true\n    end\nend",
