			["parent"] = "Crucible of Storms - GlowingFrames ",
			["displayText"] = " ",
			["customText"] = "\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
			["yOffset"] = -36.000030517578,
			["anchorPoint"] = "CENTER",
			["glow"] = false,
