			["customText"] = "function()\n    if WeakAuras.IsOptionsOpen() then\n        local icon = \"|TInterface\\\\TARGETINGFRAME\\\\UI-RaidTargetingIcon_3:12:12:0:2|t\"\n        return icon..\"HEALER -> TARGET\"\n    end\n    local res = \"\"\n    for healer, assign in pairs(aura_env.assignments) do\n        local target = assign[Zaemme]\n        local expire = assign[Yellowy]\n        local icon = assign[Yuffa]\n        local dur = expire - aura_env.GetTime()\n        local _, classHealer = aura_env.UnitClass(healer)\n        local _, classTarget = aura_env.UnitClass(target)\n        local healerStr = \"|c\"..aura_env.RAID_CLASS_COLORS[classHealer].colorStr..healer..\"|r\"\n        local targetStr = \"|c\"..aura_env.RAID_CLASS_COLORS[classTarget].colorStr..target..\"|r\"\n        local durStr = string.format(\"%.1f\", dur)\n        res = res..icon..healerStr..\" -> \"..targetStr..\" \"..durStr..\"|n\"\n    end\n    for _, foamID in ipairs(aura_env.foamOrder) do\n        for _, assign in ipairs(aura_env.foamEnv[foamID]) do\n            local target = assign[1]\n            local expire = assign[2]\n            local icon = assign[3]\n            local dur = expire - aura_env.GetTime()\n            local _, classTarget = aura_env.UnitClass(target)\n            local targetStr = \"|c\"..aura_env.RAID_CLASS_COLORS[classTarget].colorStr..target..\"|r\"\n            local durStr = string.format(\"%.1f\", dur)\n            res = res..icon..targetStr..\" \"..durStr..\"|n\"\n        end\n    end\n    return res\nend",
