						["custom"] = "function(_, ts, se, _, sGUID, sN, sF, sRF, dGUID, dN, dF, dRF, ...)\n    local e = aura_env\n    if se == \"SPELL_AURA_APPLIED\" then\n        local spellID = ...\n        dN = strsplit(\"-\", dN)\n        -- Foam applied by Guarm\n        if dGUID ~= e.UnitGUID(\"boss1\") and (spellID == e.foamFire or spellID == e.foamFrost or spellID == e.foamShadow) then\n            local breathSpellID = e.foamBreathMap[spellID]\n            -- Foam doesn't match breath\n            if not e.UnitDebuff(dN, e.GetSpellInfo(breathSpellID)) then\n                local icon = \"|TInterface\\\\TARGETINGFRAME\\\\UI-RaidTargetingIcon_\"..e.foamIconMap[spellID]..\":12:12:0:0|t\"\n                e.currentFoams[#e.currentFoams+1] = {dN, e.GetTime() + 10, icon}\n                -- Got all the debuffs\n                if #e.currentFoams == 3 then\n                    -- Prefer to assign healers to themselves\n                    for i=3, 1, -1 do\n                        local v = e.currentFoams[i]\n                        local debuffTarget = v[1]\n                        if e.isHealer[debuffTarget] then\n                            -- Not sure how WoW's Lua GC works, so just make a copy\n                            e.assignments[debuffTarget] = {v[1], v[2], v[3]}\n                            if debuffTarget == e.myName then\n                                e.Highlight(debuffTarget)\n                            end\n                            table.remove(e.currentFoams, i)\n                        end\n                    end\n                    for i, v in ipairs(e.currentFoams) do\n                        for _, healer in ipairs(e.healers) do\n                            if not e.assignments[healer] then\n                                e.assignments[healer] = {v[1], v[2], v[3]}\n                                if healer == e.myName then\n                                    e.Highlight(v[1])\n                                end\n                                break\n                            end\n                        end\n                    end\n                    e.currentFoams = {}\n                end\n            end\n        -- Foam applied by bad dispels\n        elseif dGUID ~= e.UnitGUID(\"boss1\") and (spellID == e.foamFireEnv or spellID == e.foamFrostEnv or spellID == e.foamShadowEnv) then\n            local breathSpellID = e.foamBreathMap[spellID]\n            -- Foam doesn't match breath\n            if not e.UnitDebuff(dN, e.GetSpellInfo(breathSpellID)) then\n                local t = e.foamEnv[spellID]\n                local icon = \"|TInterface\\\\TARGETINGFRAME\\\\UI-RaidTargetingIcon_\"..e.foamIconMap[spellID]..\":12:12:0:0|t\"\n                t[#t+1] = {dN, e.GetTime() + 5, icon}\n            end\n        end\n    elseif se == \"SPELL_AURA_REMOVED\" then\n        local spellID = ...\n        dN = strsplit(\"-\", dN)\n        if dGUID ~= e.UnitGUID(\"boss1\") and (spellID == e.foamFire or spellID == e.foamFrost or spellID == e.foamShadow) then\n            for healer, assign in pairs(e.assignments) do\n                if assign[1] == dN then\n                    e.assignments[healer] = nil\n                    if e.highlightedFrame and healer == e.myName then\n                        ActionButton_HideOverlayGlow(e.highlightedFrame)\n                        e.highlightedFrame = nil\n                    end\n                    break\n                end\n            end\n        elseif dGUID ~= e.UnitGUID(\"boss1\") and (spellID == e.foamFireEnv or spellID == e.foamFrostEnv or spellID == e.foamShadowEnv) then\n            local t = e.foamEnv[spellID]\n            for i=#t, 1, -1 do\n                if t[i][1] == dN then\n                    table.remove(t, i)\n                end\n            end\n        end\n    elseif se == \"UNIT_DIED\" and bit.band(sF, COMBATLOG_OBJECT_TYPE_PLAYER) then\n        dN = strsplit(\"-\", dN)\n        for healer, assign in pairs(e.assignments) do\n            if assign[1] == dN then\n                e.assignments[healer] = nil\n                if e.highlightedFrame and healer == e.myName then\n                    ActionButton_HideOverlayGlow(e.highlightedFrame)\n                    e.highlightedFrame = nil\n                end\n                break\n            end\n        end\n        for _, t in pairs(e.foamEnv) do\n            for i=#t, 1, -1 do\n                if t[i][1] == dN then\n                    table.remove(t, i)\n                end\n            end\n        end\n    end\n    return true\nend\n\n",
