d].TehrsCDs_show_allRezzes == nil and true or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allRezzes;     \nTehrsCDs_show_allInterrupts = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allInterrupts == nil and true or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allInterrupts;\n\n--RAID SETTINGS\n--The following affects whether or not certain sections are displayed in a raid instance OR if there are 20+ people in your group\nTehrsCDs_show_allExterns_inRaid = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allExterns_inRaid == nil and true or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allExterns_inRaid;\nTehrsCDs_show_allCDs_inRaid = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allCDs_inRaid == nil and true or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allCDs_inRaid;\nTehrsCDs_show_allUtility_inRaid = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allUtility_inRaid == nil and true or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allUtility_inRaid;    \nTehrsCDs_show_allImmunityCDs_inRaid = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allImmunityCDs_inRaid == nil and false or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allImmunityCDs_inRaid;       \nTehrsCDs_show_allAoECCs_inRaid = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allAoECCs_inRaid == nil and false or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allAoECCs_inRaid;    \nTehrsCDs_show_allRezzes_inRaid = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allRezzes_inRaid == nil and false or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allRezzes_inRaid; \nTehrsCDs_show_allInterrupts_inRaid = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allInterrupts_inRaid == nil and false or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allInterrupts_inRaid;\nTehrsCDs_show_raidBattleRezTimer = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_raidBattleRezTimer == nil and true or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_raidBattleRezTimer; --If true, will display how many Battle Rez charges are available, and how long until a new charge becomes ready ONLY during a RAID Encounter -- DOES NOT NEED TehrsCDs_show_allRezzes_inRaid TO BE ENABLED\nTehrsCDs_show_Ankh_inRaid = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_Ankh_inRaid == nil and true or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_Ankh_inRaid; --If true, Ankh CD will be displayed in addition to your Battle Rez timer when in a raid encounter -- DOES NOT NEED TehrsCDs_show_allRezzes_inRaid TO BE ENABLED\n\n-- DON'T CHANGE THESE VALUES, REQUIRED FOR THIS WEAKAURA TO WORK CORRECTLY\nTehrsCDs_minmaxDisplay = true\nTehrsCDs_encounterStart = false\nTehrsCDs_show_SortByTimer = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_SortByTimer == nil and false or WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_SortByTimer; \n\n-- This is just here so I can debug when shit hits the fan, you will never need to enable this\nTehrsCDs_DEBUG = false -- Master control for debug, check GroupPoll and Engine On Ini