		["Kil'Jaeden - Armageddon stacks"] = {
			["outline"] = "OUTLINE",
			["displayText"] = "%c",
			["customText"] = "function()\n    if WeakAuras.IsOptionsOpen() then\n        return \"Rain tracker\"\n    end\n    \n    --If we are in illidan phase or further than don't show anything anymore\n    if aura_env.phase >= 4 then\n        return\n    end\n    \n    if aura_env.last and (GetTime() - aura_env.last) < 1 then\n        return aura_env.display\n    end\n    \n    \n    aura_env.last = GetTime()\n    \n    local result = {}\n    for mark, players in pairs(aura_env.assignments) do\n        local absentCount = 0\n        local dotCount = 0\n        for _, player in pairs(players) do\n            \n            local _,_,_,stacks,_,_,expires = UnitDebuff(player, aura_env.dotSpellInfo) \n            \n            stacks = stacks or 0\n            \n            if expires then\n                --Take off how long it is till the next armageddon and see if they will still have the dot\n                if aura_env.armaTimers[aura_env.phase] \n                and aura_env.armaTimers[aura_env.phase][aura_env.nextArma] then\n                    --Should always go inside this if\n                    expires = expires - aura_env.armaTimers[aura_env.phase][aura_env.nextArma]\n                else\n                    expires = expires - GetTime()\n                end\n            else\n                expires = 0\n            end\n            \n            if (expires > 0 and stacks > 1) or aura_env.isPlayerAbsent(player) then \n                absentCount = absentCount + 1\n            elseif expires > 0 and stacks == 1 and not aura_env.isPlayerAbsent(player) then\n                dotCount = dotCount + 1\n            end\n            \n            if UnitIsDead(player) then\n                absentCount = absentCount + 1\n            end\n        end\n        \n        result[#result+1] = {mark=mark,dotCount=dotCount,thereCount=#players - absentCount}\n    end\n    \n    local sort = function(a, b)\n        --If there is the same amount of player there, then fallback to sort by the amount of people with dots\n        if a.thereCount == b.thereCount then\n            return a.dotCount < b.dotCount\n        else \n            return a.thereCount > b.thereCount\n        end\n    end\n    \n    table.sort(result, sort)\n    \n    local display = \"\"\n    for _, data in pairs(result) do\n        if data.thereCount < 3 then\n            if data.thereCount < 0 then\n                data.thereCount = 0\n            end\n            \n            data.thereCount = \"|c00ff2200\" .. data.thereCount .. \"|r\"\n        end\n        \n        data.mark = \"|TInterface/TargetingFrame/UI-RaidTargetingIcon_\" .. data.mark .. \":10:10:0:-15|t\"\n        data.dotCount =  \"|c00ff7607\" .. data.dotCount .. \"|r\"\n        \n        display = display..data.mark..\" - \"..data.dotCount..\"/\"..data.thereCount..\"\\n\"\n    end\n    \n    aura_env.display = display\n    return display\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
