			},
			["displayText"] = "%c",
			["customText"] = "\n\nfunction()\n    local now = GetTime()\n    if now < aura_env.update_next then\n        return aura_env.last_value\n    end\n    aura_env.update_next = now + (aura_env.update_interval/1000)\n    \n    -- colors\n    local col = {[\"red\"] = \"ff0000\", [\"orange\"] = \"ff9000\", [\"yellow\"] = \"ffff00\", [\"green\"] = \"00ff00\"}\n    -- options display\n    if WeakAuras.IsOptionsOpen() then\n        aura_env.last_value = \"|cff\"..col.yellow..\"3\"..\"|r\"\n        return aura_env.last_value\n    end\n    -- string to append and return\n    local str = \"\"\n    -- color for string\n    local str_col = \"\"\n    -- number of atonements\n    local atonement_count = select(4, UnitAura(\"PLAYER\", \"Sins of the Many\"))\n    if not atonement_count then atonement_count = 0 end\n    -- abort if there is no atoned players\n    --if atonement_count == 0 then return end\n    -- group size\n    local group_size = GetNumGroupMembers()\n    -- raid or party?\n    local group_type = IsInRaid() and \"raid\" or \"party\"\n    -- approximate smite healing\n    local heal_hit = 0 -- hit case\n    local heal_crit = 0 -- crit case\n    -- get versatility for heals\n    local vers = 1 + GetCombatRatingBonus(29) / 100\n    -- get mastery\n    local mastery = 1 + GetMasteryEffect() / 100\n    -- get atonement transfer\n    local atonement_heal = ((mastery * 40) / 100)\n    -- Check artifact for sins of the many and 35+ traits\n    local hasSins = false\n    local paragon = 1\n    local ten = 1\n    local loaded = IsAddOnLoaded(\"LibArtifactData-1.0\") or LoadAddOn(\"LibArtifactData-1.0\")    \n    if loaded then\n        aura_env.LAD = aura_env.LAD or LibStub(\"LibArtifactData-1.0\")\n        if not aura_env.LAD:GetActiveArtifactID() then\n            aura_env.LAD:ForceUpdate()            \n        end\n        local _, traits = aura_env.LAD:GetArtifactTraits()\n        if traits then\n            for _,v in ipairs(traits) do\n                if v.spellID == 198074 then\n                    hasSins = true\n                elseif v.spellID == 214925 then\n                    if v.currentRank > 0 then \n                        paragon = 1.05\n                    end\n                elseif v.spellID == 241148 then\n                    if v.currentRank > 0 then \n                        ten = 1.1\n                    end\n                end\n            end\n        end\n    end\n    \n    -- sins of the many\n    local sins = hasSins and 1 + (atonement_count / 100) or 1\n    -- twist of fate\n    local tof = UnitBuff(\"player\", GetSpellInfo(109142)) and 1.2 or 1\n    -- get smite heal from atonement\n    local smite_heal = GetSpellBonusDamage(7) * 2.25 * vers * atonement_heal * 1.15 * tof * sins * paragon\n    local sm_heal = GetSpellBonusDamage(6)*7.50 * 1.10 * vers * tof * paragon\n    sm_heal = sm_heal + sm_heal*(GetSpellCritChance(6)/100)\n    local smite_time = select(4, GetSpellInfo(585))\n    local group_start = IsInRaid() and 1 or 0 -- party doesn't include player\n    \n    -- Recompute list of healers (not including me) periodically, to avoid going thru all the group each frame\n    if (now >= aura_env.healers_next) and aura_env.check_incoming_heal then\n        aura_env.healers_next = now + (aura_env.healers_interval/1000)\n        aura_env.healers = {}\n        local j = 1\n        for i = 1, group_start + group_size - 1 do\n            local unit_id = group_type .. i\n            if (UnitGroupRolesAssigned(unit_id) == \"HEALER\") and (UnitGUID(\"player\") ~= UnitGUID(unit_id)) then\n                aura_env.healers[j] = unit_id\n                j = j + 1\n            end\n        end\n    end\n    -- loop through party\n    for i = group_start, group_start + group_size - 1 do\n        -- get unit id etc \"raid2\"\n        local unit_id = (i == 0) and \"player\" or (group_type .. i)\n        local remaining_time = select(7, UnitBuff(unit_id, GetSpellInfo(81749), nil, \"PLAYER\"))\n        remaining_time = (remaining_time == nil) and 0 or (remaining_time - now)\n        if (remaining_time*1000) > (smite_time + aura_env.reaction_time) then\n            -- get unit health defecit\n            local health_defecit = UnitHealthMax(unit_id) - UnitHealth(unit_id)\n            if aura_env.check_incoming_heal then\n                if (UnitGetIncomingHeals(unit_id)> 0) then\n                    -- incoming heal exists, try to determine source(s)\n                    local j\n                    local total_amount = 0\n                    for j = 1, #aura_env.healers do\n                        amount = UnitGetIncomingHeals(unit_id, aura_env.healers[j]) or 0\n                        if (amount > 0) then\n                            -- process only incoming heals that will land before the smite\n                            when_lands = select(6, UnitCastingInfo(aura_env.healers[j]))\n                            if when_lands ~= nil then\n                                when_lands = when_lands - now*1000\n                                if when_lands < (smite_time + aura_env.reaction_time) then\n                                    total_amount = total_amount + amount\n                                end\n                            end\n                        end\n                    end\n                    total_amount = (total_amount < health_defecit) and total_amount or health_defecit\n                    health_defecit = health_defecit - total_amount\n                end\n            end\n            -- compute the heal received by this unit\n            local this_hit = (health_defecit < smite_heal) and health_defecit or smite_heal\n            local this_crit = (health_defecit < smite_heal*2) and health_defecit or (smite_heal*2)\n            heal_hit = heal_hit + this_hit\n            heal_crit = heal_crit + this_crit\n        end\n    end\n    \n    local crit_chance = GetSpellCritChance(2)/100\n    -- compute probabilistic value for the total atonement heal\n    local heal_avg = heal_hit*(1-crit_chance) + heal_crit*crit_chance\n    --DEFAULT_CHAT_FRAME:AddMessage(\"DEBUG: avgheal=\" .. heal_avg)\n    local efficiency = (heal_avg*100) / sm_heal\n    if efficiency >= aura_env.options.green then\n        str_col = col.green\n    elseif efficiency >= aura_env.options.yellow then\n        str_col = col.yellow\n    elseif efficiency >= aura_env.options.orange then\n        str_col = col.orange\n    else\n        str_col = col.red\n    end\n    aura_env.last_value = \"|cff\"..str_col..atonement_count..\"|r\"\n    return aura_env.last_value\nend\n\n\n\n\n\n\n",
