						["spellIds"] = {
						},
						["events"] = "COMBAT_LOG_EVENT_UNFILTERED,ENCOUNTER_START,ENCOUNTER_END,TehrsCDs_ShowAll,CHALLENGE_MODE_START,TehrsCDs_DEBUGTOGGLE,TehrsCDs_ShowDisplay",
						["custom"] = "function (event, arg1, eventType, arg2, sourceGUID, sourceName, sourceFlags, sourceFlags2, destGUID, destName, destFlags, destFlags2, spellID, spellName, spellSchool, extraSpellID, extraSpellName, extraSpellSchool)\n    \n    local function getPetOwner(petName, petGUID)\n        \n        print(petName..petGUID)\n        \n        if UnitGUID(\"pet\") == petGUID then\n            return UnitName(\"player\")\n        end\n        \n        local ownerName\n        if IsInRaid() then\n            for i=1, GetNumGroupMembers() do\n                if UnitGUID(\"raid\"..i..\"pet\") == petGUID then\n                    ownerName = UnitName(\"raid\"..i)\n                    break\n                end\n            end\n        else\n            for i=1, GetNumSubgroupMembers() do\n                if UnitGUID(\"party\"..i..\"pet\") == petGUID then\n                    ownerName = UnitName(\"party\"..i)\n                    break\n                end\n            end\n        end\n        if ownerName then\n            return ownerName\n        end\n        return petName\n    end    \n    \n    if (event == \"TehrsCDs_DEBUGTOGGLE\") then -- /script WeakAuras.ScanEvents(\"TehrsCDs_DEBUGTOGGLE\")\n        TehrsCDs_DEBUG = not TehrsCDs_DEBUG\n        print(\"|cFF00A2E8Tehr's RaidCDs:|r Debugging is now \"..( TehrsCDs_DEBUG and \"|cFF00FF00ENABLED|r\" or \"|cFFFF0000DISABLED|r\" )..\"\\nNote that you need to enable either TehrsCDs_DEBUG_Engine or TehrsCDs_DEBUG_GroupPoll in their respective On Init to get debug results\")\n    end    \n    \n    if (event == \"TehrsCDs_ShowAll\") then -- /script WeakAuras.ScanEvents(\"TehrsCDs_ShowAll\")\n        TehrsCDs_show_allExterns = true\n        TehrsCDs_show_allCDs = true\n        TehrsCDs_show_allUtility = true\n        TehrsCDs_show_allImmunityCDs = true\n        TehrsCDs_show_allAoECCs = true\n        TehrsCDs_show_allInterrupts = true\n        TehrsCDs_show_allRezzes = true\n        WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allExterns = true\n        WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allCDs = true\n        WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allUtility = true\n        WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allImmunityCDs = true\n        WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allAoECCs = true\n        WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allInterrupts = true\n        WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id].TehrsCDs_show_allRezzes = true\n        \n        print(\"|cFF00A2E8Tehr's RaidCDs:|r All CDs are now |cFF00FF00ENABLED|r\")    \n        \n    elseif (event == \"TehrsCDs_ShowDisplay\") then -- /script WeakAuras.ScanEvents(\"TehrsCDs_ShowDisplay\")\n        TehrsCDs_minmaxDisplays = true\n        \n        print(\"|cFF00A2E8Tehr's RaidCDs:|r Display is now |cFF00FF00ENABLED|r\")          \n        \n    end   \n    \n    if event == \"ENCOUNTER_START\" then\n        if arg1 then\n            TehrsCDs_encounterStart = true\n            if TehrsCDs_DEBUG and TehrsCDs_DEBUG_Engine then print(\"Engine: encounter start\") end\n        end\n    elseif event == \"ENCOUNTER_END\" then\n        if arg1 then\n            TehrsCDs_encounterStart = false\n            if TehrsCDs_DEBUG and TehrsCDs_DEBUG_Engine then print(\"Engine: encounter end\") end\n            if TehrsCDs_instanceType == \"raid\" then\n                _rezCDs_dks = nil\n                _rezCDs_druids = nil\n                _rezCDs_hunters = nil\n                _rezCDs_warlocks = nil    \n                if TehrsCDs_DEBUG and TehrsCDs_DEBUG_Engine then print(\"Engine: in a raid, resetting brezzes\") end\n            end\n        end\n    end    \n    \n    