en\n            timer = timer%(self.period*k)\n        end\n        for i=1,self.N do\n            local pN = i+self.N*(k-1)\n            local position = (space*i+perimeter*timer/(self.period*k))%perimeter\n            if position>bottomlim then\n                self.sparkles[pN]: SetPoint(\"CENTER\",self,\"BOTTOMRIGHT\",-position+bottomlim,0)\n            elseif position>rightlim then\n                self.sparkles[pN]: SetPoint(\"CENTER\",self,\"TOPRIGHT\",0,-position+rightlim)\n            elseif position>height then\n                self.sparkles[pN]: SetPoint(\"CENTER\",self,\"TOPLEFT\",position-height,0)\n            else\n                self.sparkles[pN]: SetPoint(\"CENTER\",self,\"BOTTOMLEFT\",0,position)\n            end\n        end\n    end\nend\n\nACShine_Start = function(region,r,g,b,N,period,scale)\n    r = r or 0.95\n    g = g or 0.95\n    b = b or 0.32\n    if N then\n        if not(N>0) then\n            N = 4\n        end\n    else\n        N = 4\n    end\n    \n    scale = scale or 1\n    \n    if not region.ACShine then\n        local frame,new =  AutoCastShineFramePool:Acquire()\n        if new then\n            frame.sparkles = {}\n            frame.timers = {0,0,0,0}\n        end\n        region.ACShine = frame\n    end\n    local f = region.ACShine\n    f:SetParent(region)\n    f:SetFrameLevel(region:GetFrameLevel()+4)\n    f:SetAllPoints(region)\n    f:Show()\n    \n    local width,height = f:GetSize();\n    local space =  2*(width+height)/N\n    local sizes ={13,10,7,4}\n    \n    if period then\n        if not(period>0.4) then\n            period = 8\n        end\n    else\n        period = 8\n    end\n    \n    \n    for k,size in pairs(sizes) do\n        for i = 1,N do\n            local pN = i+(k-1)*N\n            if not f.sparkles[pN] then\n                local sparkle,new = AutoCastShineTexPool:Acquire()\n                if new then\n                    sparkle:SetTexture(\"Interface\\\\Challenges\\\\challenges-metalglow\")\n      