            display = ilvl,\n                        changed = true,\n                        show = true,\n                    }\n                    count = count + 1\n                end\n            end\n        end\n    end\n    \n    if aura_env.showTrinkets then --creating trinkets\n        for i = 1, 2 do\n            local trinketSlot = 12 + i\n            \n            local itemID = GetInventoryItemLink(\"player\", trinketSlot)\n            if itemID then\n                local name, _, quality, ilvl = GetItemInfo(itemID)\n                local icon = GetItemIcon(itemID)\n                if quality ~= 5 and name then\n                    local ilvl = ITEM_QUALITY_COLORS[quality].hex..ilvl..\"|r\"\n                    states[count] = {\n                        name = ITEM_QUALITY_COLORS[quality].hex..name..\"|r\",\n                        icon = icon,\n                        count = count,\n                        link = itemID,\n                        ilvl = ilvl,\n                        display = ilvl,\n                        description = \"\",\n                        changed = true,\n                        show = true,\n                    }\n                    count = count + 1\n                end\n            end\n        end\n    end\n    \n    if aura_env.showAmulet then  --creating azerite amulet\n        local HeartOfAzeroth = C_AzeriteItem.FindActiveAzeriteItem()\n        if HeartOfAzeroth then\n            local azeritePowerLevel = C_AzeriteItem.GetPowerLevel(HeartOfAzeroth)\n            --local specID = GetSpecializationInfo(GetSpecialization())\n            local itemSlot = HeartOfAzeroth.equipmentSlotIndex\n            local itemID = GetInventoryItemLink(\"player\", itemSlot)\n            if itemID then\n                local name, _, quality, ilvl = GetItemInfo(itemID)\n                local icon = GetItemIcon(itemID)\n                if name then\n                    local ilvl = ITEM_QUALITY_COLORS[quality].hex..ilvl..\"|r\"\n                    local plvl = ITEM_QUALITY_COLORS[quality].hex..\"[\"..azeritePowerLevel..\"]|r\"\n                    states[count] = {\n                        name = ITEM_QUALITY_COLORS[quality].hex..name..\"|r\",\n                        icon = icon,\n                        count = count,\n                        link = itemID,\n                        description = \"\",\n                        ilvl = ilvl,\n                        plvl = plvl,\n                        display = plvl .. \"|n\" .. ilvl,\n                        changed = true,\n                        show = true,\n                    }\n                    count = count + 1\n                end\n            end\n        end\n    end\n    \n    if aura_env.showAzeriteEmpoweredItems then --creating azerite armor talents\n        local specID = GetSpecializationInfo(GetSpecialization())\n        for _, slot in next, {1,3,5} do\n            local item = Item:CreateFromEquipmentSlot(slot)\n            if (not item:IsItemEmpty()) then\n                local itemLocation = item:GetItemLocation()\n                if (C_AzeriteEmpoweredItem.IsAzeriteEmpoweredItem(itemLocation)) then\n                    local itemID = GetInventoryItemLink(\"player\", slot)\n                    local name, _, quality, ilvl = GetItemInfo(itemID)\n                    name = name or \"\"\n                    ilvl = ilvl or 1\n                    quality = quality or 2\n                    local icon = GetItemIcon(itemID)\n                    local itemSlot = slot == 1 and \"HEAD\" or slot == 3 and \"SHOULDERS\" or slot == 5 and \"CHEST\"\n                    local ilvl = ITEM_QUALITY_COLORS[quality].hex..ilvl..\"|r\"\n                    states[count] = {\n                        name = ITEM_QUALITY_COLORS[quality].hex..name..\"|r\",\n                        icon = icon,\n                        count = count,\n                        link = itemID,\n                        description = \"\",\n                        ilvl = ilvl,\n                        itemSlot = itemSlot,\n                        display = ilvl,\n                        changed = true,\n                        show = true,\n                    }\n                    count = count + 1\n                    local circle = 1\n                    local tierInfo = C_AzeriteEmpoweredItem.GetAllTierInfo(itemLocation)\n                    for tier, info in next, tierInfo do\n                        \n                        for _, powerID in next, info.azeritePowerIDs do\n                            local powerInfo = C_AzeriteEmpoweredItem.GetPowerInfo(powerID)\n                            local id = name..powerInfo.spellID\n                            local spellName, _, spellIcon = GetSpellInfo(powerInfo.spellID)\n                            if powerInfo.spellID == 263978 then\n                                spellIcon = aura_env.upgradeTexture\n                            end\n                            local description = GetSpellDescription(powerInfo.spellID)\n                            local canSelect = C_AzeriteEmpoweredItem.CanSelectPower(itemLocation, powerID)\n                            if powerID and specID and C_AzeriteEmpoweredItem.IsPowerAvailableForSpec(powerID, specID) and (C_AzeriteEmpoweredItem.IsPowerSelected(itemLocation, powerID) or canSelect) then\n                                local ilvl2 = \"|cFFbbbbbbT\"..circle..\"|r\"\n                                if powerInfo.spellID == 263978 and not canSelect then\n                                    if aura_env.showUpgrade then\n                                        states[id] = {\n                                            name = spellName,\n                                            icon = spellIcon,\n                                            spellid = powerInfo.spellID,\n                                            count = count,\n                                            description = description,\n                                            ilvl = ilvl2,\n                                            canSelect = canSelect,\n                                            relativeTo = itemSlot,\n                                            display = ilvl2,\n                                            changed = true,\n                                            show = true,\n                                        }\n                                    end\n                                else\n                                    states[id] = {\n                                        name = spellName,\n                                        icon = spellIcon,\n                                        spellid = powerInfo.spellID,\n                                        count = count,\n                                        description = description,\n                                        ilvl = ilvl2,\n                                        canSelect = canSelect,\n                                        relativeTo = itemSlot,\n                                        display = ilvl2,\n                                        changed = true,\n                                        show = true,\n                                    }\n                                    count = count + 1\n                                end\n                            end\n                        end\n                        circle = circle + 1\n                        if aura_env.showOnlyT1Azerite and tier == 1 then break end\n                    end\n                end\n            end\n        end\n    end\n    \n    aura_env.flag = true\n    return true\nend\n\n--[[\nC_AzeriteEmpoweredItem={ \n[18:26:35]   CanSelectPower=<function>, \n[18:26:35]   HasAnyUnselectedPowers=<function>, \n[18:26:35]   IsAzeriteEmpoweredItemByID=<function>, \n[18:26:35]   GetAllTierInfoByItemID=<function>, \n[18:26:35]   IsPowerAvailableForSpec=<function>, \n[18:26:35]   IsAzeriteEmpoweredItem=<function>, \n[18:26:35]   IsPowerSelected=<func