					["custom"] = "-- LAD v1.0 by Rennae-MoonGuard x)\nlocal Me = aura_env\n\n-------------------------------------------------------------------------------\nMe.laps = 0\nMe.node = 0\nMe.progress = 0\nMe.direction = nil -- nil = either, 1 = clockwise, 2 = anticlockwise\n\n-------------------------------------------------------------------------------\n-- y, x, radius\nMe.nodes = {\n    {-935.9,4468.1,40};\n    {-859.6, 4545.8, 31};\n    {-762.3, 4476.7, 30.3};\n    {-836,4391,19};\n}\n\n-------------------------------------------------------------------------------\nlocal function in_node( node )\n    local y, x = UnitPosition( \"player\" )\n    y = y - node[1]\n    x = x - node[2]\n    return y*y + x*x < node[3] * node[3]\nend\n\n-------------------------------------------------------------------------------\nfunction Me.ReachedNode()\n    if Me.node == 0 then\n        Me.laps = Me.laps + 1 \n        -- play sound?\n    end\n    \nend\n\n-------------------------------------------------------------------------------\nfunction Me.Update()\n    local a = Me.nodes[ 1 + ((Me.node + 1) % #Me.nodes) ]\n    local b = Me.nodes[ 1 + ((Me.node - 1) % #Me.nodes) ]\n    \n    if in_node(a) and (Me.direction or 1) == 1 then\n        Me.node = (Me.node + 1) % #Me.nodes\n        Me.ReachedNode()\n        Me.direction = 1\n    elseif in_node(b) and (Me.direction or 2) == 2 then\n        Me.node = (Me.node - 1) % #Me.nodes\n        Me.ReachedNode()\n        Me.direction = 2\n    end\n    \n    if in_node( Me.nodes[1] ) then\n        Me.direction = nil\n        Me.node = 0\n    end\nend",
